            "name": "任务投放",
            "orderSeq": 2,
            "target": "/task",
            "handleRight": "HR_CUSTOMIZED_ROBOT;HR_TRIAL_GROUP",
            "status": 1,
            "parentId": "2617d635-2520-4cab-afef-74fd6d3754c8",
            "selected": null,
            "children": null
        }]
    },
    {
        id: '6',
        "code": "finance",
        "name": "财务",
        "orderSeq": 4,
        "target": "/finance",
        "status": 1,
        "selected": null,
        "children": [
            {
                id: '11',
                "code": "finance",
                "name": "资金流水",
                "orderSeq": 1,
                "target": "/capital",
                "handleRight": "HR_CUSTOMIZED_ROBOT;HR_TRIAL_GROUP",
                "status": 1,
                "parentId": "2617d635-2520-4cab-afef-74fd6d3754c8",
                "selected": null,
                "children": null
            },
            {
                id: '12',
                "code": "finance",
                "name": "开票信息",
                "orderSeq": 2,
                "target": "/receipt",
                "handleRight": "HR_CUSTOMIZED_ROBOT;HR_TRIAL_GROUP",
                "status": 1,
                "parentId": "2617d635-2520-4cab-afef-74fd6d3754c8",
                "selected": null,
                "children": null
            }
        ]
    }, {
        id: '7',
        "code": "account",
        "name": "账户",
        "orderSeq": 4,
        "target": "/account",
        "handleRight": "HR_CUSTOMIZED_ROBOT;HR_TRIAL_GROUP",
        "status": 1,
        "selected": null,
        "children": [{
            id: '8',
