            "name": "账户信息",
            "orderSeq": 1,
            "target": "/info",
            "handleRight": "HR_CUSTOMIZED_ROBOT;HR_TRIAL_GROUP",
            "status": 1,
            "parentId": "2617d635-2520-4cab-afef-74fd6d3754c8",
            "selected": null,
            "children": null
        }, {
            id: '9',
            "code": "UMScope",
            "name": "密码修改",
            "orderSeq": 2,
            "target": "/password",
            "handleRight": "HR_CUSTOMIZED_ROBOT;HR_TRIAL_GROUP",
            "status": 1,
            "parentId": "2617d635-2520-4cab-afef-74fd6d3754c8",
            "selected": null,
            "children": null
        },
            {
                id: '10',
                "code": "UMScope",
                "name": "资质管理",
                "orderSeq": 2,
                "target": "/condition",
                "handleRight": "HR_CUSTOMIZED_ROBOT;HR_TRIAL_GROUP",
                "status": 1,
                "parentId": "2617d635-2520-4cab-afef-74fd6d3754c8",
                "selected": null,
                "children": null
            }
        ]
    }
]
const SubNavi = ({ styles, navi, subNavi, expand, history }) => {
    return (
        <div className={''} style={{ height: expand ? subNavi.length * 36 + 'px' : 0 }}>
            {
                subNavi.map((v, i) => <div key={i} onClick={() => { history.push(navi.target + v.target) }}>{v.name}</div>)
            }
        </div>
    )
}
class NaviItem extends Component {
    constructor(props) {
        super(props)
    }
    clickHandle = () => {
        // const { navi, expandHandle, history } = this.props
        // if (navi.children == null) {
        //     history.push(navi.target)
        // }
        // expandHandle(navi.id)
    }
    render() {
        const { navi, history } = this.props
        const activeFlag = history.location.pathname.includes(navi.target)
        return (
            <div className={''}>
                {/*<div className={`${styles["naviText"]} ${activeFlag ? styles["active"] : ''} ${navi.selected ? styles["expand"] : ''}`} onClick={this.clickHandle}>*/}
                    {/*<div className={`${styles["navi-icon"]} ${styles[dataMap[navi.code]]}`}></div>{navi.name}*/}
                {/*</div>*/}
                {navi.children ? <SubNavi styles={styles} navi={navi} subNavi={navi.children} expand={navi.selected} history={history} /> : ''}
            </div>
        )
    }
}

export default class SideNavi extends Component{
    constructor(props){
        super(props)
        this.state = {
            naviInfo: naviInfo
        }
    }
    expandHandle=()=>{

    }
    render(){
        const {history} =this.props
        return <div className='naviBox'>
            <div className="avatar">
                <img src="http://img.zcool.cn/community/01ee0d5544f1380000019ae9895b3f.jpg@1280w_1l_2o_100sh.jpg" alt="" />
                <p>群宠管理系统</p>
            </div>
            <div className="navi">
                {
                    naviInfo.map((navi, i) => {
                        return <NaviItem key={i} navi={navi} expandHandle={this.expandHandle} history={history} />
                    })
                }
            </div>
        </div>
    }
}
