                "selected": null,
                "children": null
            }
        ]
    }
]

const dataMap = {
    home: 'icon-home',
    delivery: 'icon-delivery',
    dashboard: 'icon-data',
    finance: 'icon-wealth',
    account: 'icon-account'
}
const SubNavi = ({ navi, subNavi, expand, history }) => {
    console.log(subNavi,'subNavi==')
    return (
        <div className={'subNavi'} style={{ height: expand ? subNavi.length * 36 + 'px' : 0 }}>
            {
                subNavi.map((v, i) => <div className='subNaviItem' key={i} onClick={() => { history.push(navi.target + v.target) }}>{v.name}</div>)
            }
        </div>
    )
}
class NaviItem extends Component {
    constructor(props) {
        super(props)
    }
    clickHandle = () => {
        // const { navi, expandHandle, history } = this.props
        // if (navi.children == null) {
        //     history.push(navi.target)
        // }
        // expandHandle(navi.id)
    }
    render() {
        const { navi, history } = this.props
        const activeFlag = history.location.pathname.includes(navi.code)
        console.log(navi.code,'navi.target====')
        console.log(navi,activeFlag,'navi==')
        //code
        return (
            <div className={'naviItem'}>
                <div className="naviText">
                    <span className={`navi-icon ${dataMap[navi.code]} ${activeFlag?'active':''}`}/>
                    <span>{navi.name}</span>
                </div>
                {navi.children ? <SubNavi navi={navi} subNavi={navi.children} expand={navi.selected} history={history} /> : ''}
            </div>
        )
    }
}

export default class SideNavi extends Component{
    constructor(props){
        super(props)
        this.state = {
            naviInfo: naviInfo
        }
    }
    expandHandle=()=>{

    }
    render(){
        const {history} =this.props
        return <div className='naviBox'>
            <div className="avatar">
                <img src="http://img.zcool.cn/community/01ee0d5544f1380000019ae9895b3f.jpg@1280w_1l_2o_100sh.jpg" alt="" />
                <p>群宠管理系统</p>
            </div>
            <div className="navi">
                {
                    naviInfo.map((navi, i) => {
                        return <NaviItem key={i} navi={navi} expandHandle={this.expandHandle} history={history} />
                    })
                }
            </div>
        </div>
    }
}
